<% } else { %>
  <span><%= flight.outbound_flight_airport_a %> ‚Üí <%= flight.outbound_flight_airport_b %></span>
<% } %>
            <span><%= flight.outboud_flight_date_time %></span>
          </div>
          <div style="color: #5d6481; text-align: center"><%= order.Flight.quantity %></div>
          <div style="color: #5d6481; text-align: center">‚Ç¨ <%= formatNumber(flight.system_cost / order.Flight.quantity) %></div>
          <strong style="color: #19213d">‚Ç¨ <%= flight.system_cost %></strong>
        </div>
      <% } %>

