            <strong><%= hotel.name %></strong>
            <% hotel.rooms.forEach(function(room) { %>
              <span><%= room.type %> kamer met <%= room.quantity %> x <%= room.beds %></span>
            <% }); %>

            <% if (hotel?.breakfast_include) { %>
              <span> Het ontbijt is inbegrepen in de prijs.</span>
            <% } %>

            <span><%= hotel.numOfNights %> nacht(en)</span>
          </div>
          <div style="color: #5d6481; text-align: center"><%= hotel.quantity %></div>
          <div style="color: #5d6481; text-align: center">‚Ç¨ <%= formatNumber(hotel.system_cost / hotel.quantity) %></div>
