              <strong style="color: #1a1c21">Servicepakket</strong>
              <span><%= order.warranty.name %></span>
            </div>
            <div style="color: #5d6481; text-align: center"><%= order.quote.quantity %></div>
            <div style="color: #5d6481; text-align: center">‚Ç¨ <%= order.warranty.price %></div>
            <strong style="color: #19213d">‚Ç¨ <%= order.warranty.total %></strong>
        </div>
        <% if (order.additional_items) { %>
        <% order.additional_items.forEach(function(item) { %>
        <div class="table-row">
        <div
            style="
            display: flex;
            flex-direction: column;
            gap: 2px;
            color: #5e6470;
            "
        >
            <strong style="color: #1a1c21">Extra items</strong>
            <span><%= item.description.split("\n").map(item => item ).join("<br/>") %></span>
