        <div style="color: #5d6481; text-align: center">‚Ç¨ <%= item.price %></div>
        <strong style="color: #19213d">‚Ç¨ <%= item.price * item.quantity %></strong>
        </div>
        <% }); %>
        <% } %>

        <% if (order.order.coupon_value) { %>
          <div class="table-row">
            <div
              style="
                display: flex;
                flex-direction: column;
                gap: 2px;
                color: #5e6470;
              "
            >
              <strong style="color: #1a1c21">Coupon</strong>
              <span><%= order.order.coupon_code %></span>
            </div>
            <div style="color: #5d6481; text-align: center"></div>
            <div style="color: #5d6481; text-align: center"></div>
