INSERT INTO public.templates (id, name, content, subject, created_at, updated_at, created_by_id, updated_by_id, locale, description, viewer) VALUES (43, 'qr_contract_pdf', '<!DOCTYPE html>
<html lang="nl">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Voetbaltrips.com Bevestiging</title>
    <style>
      body {
        font-family: Arial, sans-serif;
        margin: 0;
        padding: 0;
        font-size: 12px;
        position: relative;
        background-color: white;
        /* Standard A4 is 210mm √ó 297mm which is approximately 794px √ó 1123px at 96 DPI */
        width: 794px;
        height: 1123px;
      }

      .page {
        width: 794px;
        height: 1123px;
        position: relative;
        padding: 32px;
        box-sizing: border-box;
        background-color: white;
        margin: 0;
      }

      .header {
        display: flex;
        justify-content: space-between;
        margin-bottom: 20px;
      }

      .company-info {
        flex: 1;
      }

      .logo {
        flex: 1;
        text-align: right;
      }

      .logo img {
        width: 250px;
      }

      .divider {
        border-bottom: 1px solid #ddd;
        margin: 20px 0;
      }

      .customer-details,
      .traveler-details,
      .booking-details {
        margin-bottom: 20px;
        line-height: 10px;
      }

      .main-content {
        margin-top: 30px;
      }

      h3 {
        margin-bottom: 5px;
        font-weight: bold;
      }

      p {
        margin: 3px 0;
      }

      .footer {
        margin-top: 30px;
        font-size: 11px;
      }

      .confirmation-text {
        margin-bottom: 15px;
        text-align: justify;
      }

      /* For PDF rendering */
      @page {
        margin: 0;
      }
    </style>
  </head>
