INSERT INTO public.templates (id, name, content, subject, created_at, updated_at, created_by_id, updated_by_id, locale, description, viewer) VALUES (94, 'credit_invoice_pdf', '<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Credit Invoice #<%= creditInvoice.id %></title>
    <style>
      body {
        font-family: "Inter", sans-serif;
        background-color: #016feb;

        margin: 0;
        padding: 0;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 100vh;
        padding: 0 25px;
      }
      .header {
        color: white;
        font-size: 8px;
        padding: 25px 25px 33px 25px;
        display: flex;
        justify-content: space-between;
        align-items: end;
      }
      .invoice-table {
        background-color: white;
        width: 100%;
        min-height: 510px;
        height: auto;
        border-radius: 12px;
        border: 0.5px solid #d7dae0;
        flex-grow: 1;
        display: flex;
        flex-direction: column;
      }
      .credit-notice {
        background-color: #f8d7da;
        border: 1px solid #f5c6cb;
        color: #721c24;
        padding: 15px 30px;
        border-radius: 12px 12px 0 0;
        font-weight: bold;
        font-size: 12px;
        text-align: center;
      }
      .reference-info {
        background-color: #e9ecef;
        padding: 15px 30px;
        border-bottom: 1px solid #d7dae0;
        font-size: 9px;
      }
      .table-header,
      .table-row {
        display: grid;
        grid-template-columns: 2fr 0.5fr 1fr auto;
        gap: 1px;
        font-size: 10px;
        padding: 18px 30px;
        text-align: start;
        align-items: center;
      }
      .table-header {
        background-color: #eff6ff;
        border-radius: 12px 12px 0 0;
        font-size: 8px;
        padding: 12px 30px;
      }
      .table-row {
        background-color: white;
        border-bottom: 0.5px solid #d7dae0;
      }
      .matches-section {
        background-color: #f8f9fa;
        border-top: 1px solid #d7dae0;
        padding: 20px 30px;
        font-size: 9px;
      }
      .matches-header {
        font-weight: 600;
        color: #1a1c21;
        margin-bottom: 10px;
        font-size: 10px;
      }
      .match-item {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 8px 0;
        border-bottom: 1px solid #e9ecef;
      }
      .match-item:last-child {
        border-bottom: none;
      }
      .match-details {
        flex: 1;
      }
      .match-name {
        font-weight: 500;
        color: #1a1c21;
        margin-bottom: 2px;
      }
      .match-category {
        color: #5e6470;
        font-size: 8px;
      }
      .match-price {
        font-weight: 500;
        color: #dc3545;
      }
      .negative-amount {
        color: #dc3545;
        font-weight: bold;
      }
      .total-row {
        display: grid;
        grid-template-columns: 2fr 0.7fr auto;
        gap: 1px;
        padding: 18px 30px;
        align-items: center;
        background-color: #f8d7da;
      }
      .footer {
        color: white;
        display: flex;
        flex-direction: column;
        padding: 30px 16px 16px 16px;
        border-radius: 0 0 8px 8px;
        min-height: 300px;
        font-size: 10px;
      }
      h5 {
        font-weight: 700;
        margin: 0 0 4px 0;
      }
      ul {
        padding-left: 15px;
        margin: 0;
        line-height: 14px;
      }
      strong {
        font-weight: 600;
        opacity: 100% !important;
      }
      .flex {
        display: flex;
        justify-content: space-between;
        align-items: center;
      }
    </style>
  </head>
  <body>
    <% function formatNumber(num) {
      // Check if the number has decimals
      if (num % 1 !== 0) {
        return num.toFixed(2);
      } else {
        return num;
      }
    } %>
    <!-- Header -->
    <div class="header">
      <div>
        <strong style="font-size: 10px; text-transform: capitalize;"><%= customer.first_name || '''' %> <%= customer.last_name || '''' %></strong>
        <div
          style="
            padding: 8px 0 0 0;
            opacity: 90%;
            margin-bottom: 0;
            gap: 2px;
            display: flex;
            flex-direction: column;
            line-height: 14px;
          "
        >
          <% if (customer.company_name) { %>
          <span><%= customer.company_name %></span>
          <% } %>
          <% if (customer.vat_number) { %>
          <span><%= customer.vat_number %></span>
          <% } %>
          <% if (customer.address) { %>
          <span><%= customer.address %></span>
          <% } %>
          <% if (customer.postal || customer.city) { %>
          <span><%= (customer.postal || '''') %><%= customer.postal && customer.city ? '', '' : '''' %><%= (customer.city || '''') %></span>
          <% } %>
          <% if (customer.country_code) { %>
          <span><%= customer.country_code %></span>
          <% } %>
        </div>
      </div>
      <div style="display: flex; gap: 32px; align-items: end">
        <div
          style="
            gap: 2px;
            display: flex;
            flex-direction: column;
            line-height: 11.25px;
          "
        >
          <div>
            <strong>Credit&nbsp;Invoice:</strong>
            <span style="opacity: 90%"><%= creditInvoice.id %></span>
          </div>
          <div>
            <strong>Credit&nbsp;Date:</strong>
            <span style="opacity: 90%"><%= creditInvoice.creditDate || '''' %></span>
          </div>
          <div>
            <strong>Original&nbsp;Invoice:</strong>
            <span style="opacity: 90%">#<%= originalInvoice.id %></span>
          </div>
          <div>
            <strong>Original&nbsp;Date:</strong>
            <span style="opacity: 90%"><%= originalInvoice.invoiceDate || '''' %></span>
          </div>
        </div>

        <div
          style="
            padding: 0 25px 0 0;
            gap: 2px;
            display: flex;
            flex-direction: column;
            font-weight: 400;
            line-height: 12px;
          "
