    <!-- Invoice Table -->
    <div class="invoice-table">
      <div class="table-header">
        <span style="font-weight: 500">Description</span>
        <span style="font-weight: 500; text-align: center">Quantity</span>
        <span style="font-weight: 500; text-align: center">Price per person</span>
        <span style="font-weight: 500">Total</span>
      </div>
      <% matches.forEach(function(match) { %>
        <div class="table-row">
          <div
            style="
              display: flex;
              flex-direction: column;
              gap: 2px;
              color: #5e6470;
            "
          >
            <strong style="color: #1a1c21">Ticket</strong>
            <strong><%= match.name %> (<%= match.competition%>)</strong>
            <span><%= match.category %></span>
            <%
                const categories = [
                  { price: match.cat_1_price, quantity: match.cat_1_quantity, name: ''Category 1'' },
                  { price: match.cat_2_price, quantity: match.cat_2_quantity, name: ''Category 2'' },
                  { price: match.cat_3_price, quantity: match.cat_3_quantity, name: ''Category 3'' },
                  { price: match.cat_4_price, quantity: match.cat_4_quantity, name: ''Category 4'' }
                ];

                // remove selected category -- so that we don''t display it as "upgrade for surcharge"
                const result = categories.findIndex(item => item.name.replace(/\D/g, "") == match.category.replace(/\D/g, ""))
                if (result != -1) delete categories[result];

                if (match.cat_1 == false) delete categories[0];
                if (match.cat_2 == false) delete categories[1];
                if (match.cat_3 == false) delete categories[2];
                if (match.cat_4 == false) delete categories[3];

                categories.forEach(category => {
                  if (category.price && category.quantity && category.price > 0 && category.quantity > 0 && category.price - match.ticketPrice !== 0) {
                %>
                    <span><strong><%= category.name %></strong> - upgrade for surcharge of ‚Ç¨ <%= (category.price - match.ticketPrice) %> p.p.</span>
                <%
                  }
                });
                %>
          </div>
          <div style="color: #5d6481; text-align: center"><%= order.quote.quantity %></div>
          <div style="color: #5d6481; text-align: center">‚Ç¨ <%= match.ticketPrice %></div>
          <strong style="color: #19213d">‚Ç¨ <%= match.totalPrice %></strong>
        </div>
      <% }); %>

      <% if (containsFlight) { %>
        <div class="table-row">
          <div
            style="
              display: flex;
              flex-direction: column;
              gap: 2px;
              color: #5e6470;
            "
          >
            <strong style="color: #1a1c21">Flights</strong>
            <strong>Outgoing flight</strong>

<% if (flight.outgoing_layover_switch) { %>
  <span><%= flight.inbound_flight_airport_a %> ‚Üí <%= flight.outgoing_layover %> (Layover: <%= flight.outgoing_layover_duration %> hours) ‚Üí <%= flight.inbound_flight_airport_b %></span>
<% } else { %>
  <span><%= flight.inbound_flight_airport_a %> ‚Üí <%= flight.inbound_flight_airport_b %></span>
<% } %>

            <span><%= flight.inboud_flight_date_time %></span>
            <br>
            <strong>Return flight</strong>

<% if (flight.return_layover_switch) { %>
  <span><%= flight.outbound_flight_airport_a %> ‚Üí <%= flight.return_layover %> (Layover: <%= flight.return_layover_duration %> hours) ‚Üí <%= flight.outbound_flight_airport_b %></span>
<% } else { %>
  <span><%= flight.outbound_flight_airport_a %> ‚Üí <%= flight.outbound_flight_airport_b %></span>
<% } %>


            <span><%= flight.outboud_flight_date_time %></span>
          </div>
          <div style="color: #5d6481; text-align: center"><%= order.Flight.quantity %></div>
          <div style="color: #5d6481; text-align: center">‚Ç¨ <%= formatNumber(flight.system_cost / order.Flight.quantity) %></div>
          <strong style="color: #19213d">‚Ç¨ <%= flight.system_cost %></strong>
        </div>
      <% } %>

      <% if (containsHotel) { %>
      <% order.hotel.forEach(hotel => { %>
        <div class="table-row">
          <div
            style="
              display: flex;
              flex-direction: column;
              gap: 2px;
              color: #5e6470;
            "
          >
            <strong style="color: #1a1c21">Hotel</strong>
            <strong><%= hotel.name %></strong>
            <% hotel.rooms.forEach(function(room) { %>
              <span><%= room.type %> room with <%= room.quantity %> x <%= room.beds %></span>
            <% }); %>
            <span><%= hotel.numOfNights %> night(s) | <%= hotel.departure_hotel_date %> - <%= hotel.return_hotel_date %> </span>

            
            <% if (hotel?.breakfast_include) { %>
              <span>The breakfast is included in the price.</span>
