    <!-- Factuurtabel -->
    <div class="invoice-table">
        <div class="table-header">
            <span style="font-weight: 500">Omschrijving</span>
            <span style="font-weight: 500; text-align: center">Aantal</span>
            <span style="font-weight: 500; text-align: center">Prijs per persoon</span>
            <span style="font-weight: 500">Totaal</span>
        </div>
        <% matches.forEach(function(match) { %>
            <div class="table-row">
                <div
                    style="
                        display: flex;
                        flex-direction: column;
                        gap: 2px;
                        color: #5e6470;
                    "
                >
                    <strong style="color: #1a1c21">Ticket</strong>
                    <strong><%= match.homeClub %> -  <%= match.awayClub %> (<%= match.competition%>)</strong>
                    <span><%= match.category %></span>
                    
                </div>
                <div style="color: #5d6481; text-align: center"><%= order.quantity %></div>
                <div style="color: #5d6481; text-align: center">‚Ç¨ <%= match.ticketPrice %></div>
                <strong style="color: #19213d">‚Ç¨ <%= match.totalPrice %></strong>
            </div>
        <% }); %>
        <% if (flight) { %>
        <div class="table-row">
            <div 
            style="
            display: flex;
            flex-direction: column;
            gap: 2px;
            color: #5e6470;
            ">
                <strong style="color: #1a1c21">Reis</strong>
                <strong>Heenvlucht</strong>
                <span><%= flight.outbound_flight_airport_a %> <%= flight.outbound_flight_airport_b %></span>
                <span><%= flight.outboud_flight_date_time %></span>
                <br>
                <strong>Terugvlucht</strong>
                <span><%= flight.inbound_flight_airport_a %> <%= flight.inbound_flight_airport_b %></span>
                <span><%= flight.inboud_flight_date_time %></span>
            </div>
            <div style="color: #5d6481; text-align: center"><%= order.quantity %></div>
            <div style="color: #5d6481; text-align: center">‚Ç¨ <%= flight.system_cost %></div>
            <strong style="color: #19213d">‚Ç¨ <%= flight.system_cost * order.quantity %></strong>
        </div>
        <% } %>

        <% if (order.order_type == "deal" && order.Stays && order.Stays.length) { %>
            <% order.Stays.forEach(function(stay) { %>
                <% stay.hotels.forEach(function(hotel) { %>
                    <div class="table-row"
                    >
                        <div style="display: flex; flex-direction: column; gap: 2px; color: #5e6470;">
                            <strong style="color: #1a1c21">Hotel</strong>
                            <strong><%= hotel.hotel.title %> (<%= hotel.hotel.star_rating %> *)</strong>
                        </div>
                        <div style="color: #5d6481; text-align: center"><%= order.quantity %></div>
                        <div style="color: #5d6481; text-align: center">‚Ç¨ <%= order.quantity > 1 ? hotel.system_cost : hotel.system_cost * 2 %></div>
                        <strong style="color: #19213d">‚Ç¨ <%= order.quantity > 1 ? hotel.system_cost * order.quantity : hotel.system_cost * 2 %></strong>
                    </div>
                <% }); %>
            <% }); %>
        <% } %>

        <% if (order.order_type == "deal" && (!order.Stays || !order.Stays.length) && order.Hotel && order.Hotel.length) { %>
            <% order.Hotel.forEach(function(hotel) { %>
                    <div class="table-row"
                    >
                        <div style="display: flex; flex-direction: column; gap: 2px; color: #5e6470;">
                            <strong style="color: #1a1c21">Hotel</strong>
                            <strong><%= hotel.hotel.title %> (<%= hotel.hotel.star_rating %> *)</strong>
                            <% if (hotel.rooms && hotel.rooms.length) { %>
                                <% hotel.rooms.forEach(function(room) { %>
                                <span>Kamer(<%= room.quantity %>) <%= room.type %>- <%= room.beds %></span>
                                <% }); %>
                            <% } %>
                            <% if (order.numOfDays) { %>
                                <span><%= order.numOfDays %> nacht(en)</span>
                            <% } %>
                        </div>
                        <div style="color: #5d6481; text-align: center"><%= order.quantity %></div>
                        <div style="color: #5d6481; text-align: center">‚Ç¨ <%= order.quantity > 1 ? hotel.system_cost : hotel.system_cost * 2 %></div>
                        <strong style="color: #19213d">‚Ç¨ <%= order.quantity > 1 ? hotel.system_cost * order.quantity : hotel.system_cost * 2 %></strong>
                    </div>
            <% }); %>
            <% } %>

        <div class="table-row">
            <div
                style="
                display: flex;
                flex-direction: column;
                gap: 2px;
                color: #5e6470;
                "
            >
                <strong style="color: #1a1c21">Servicepakket</strong>
                <span><%= order.warranty.name %></span>
            </div>
            <div style="color: #5d6481; text-align: center"><%= order.quantity %></div>
            <div style="color: #5d6481; text-align: center">‚Ç¨ <%= order.warranty.price %></div>
            <strong style="color: #19213d">‚Ç¨ <%= order.warranty.total %></strong>
        </div>

    <% if (order.order_type == "deal" && order.vzrTotal && (flight || order.Hotel || order.Stays) ) { %>
      <div class="table-row">
        <div
          style="
            display: flex;
            flex-direction: column;
            gap: 2px;
            color: #5e6470;
          "
        >
          <strong style="color: #1a1c21">VZR</strong>
        </div>
        <div style="color: #5d6481; text-align: center"></div>
        <div style="color: #5d6481; text-align: center"></div>
        <strong style="color: #19213d">‚Ç¨ <%= order.vzrTotal %></strong>
      </div>
    <% } %>

    <% if (order.coupon_value) { %>
        <div class="table-row">
            <div
            style="
                display: flex;
                flex-direction: column;
                gap: 2px;
                color: #5e6470;
                "
            >
            <strong style="color: #1a1c21">Coupon</strong>
            <span><%= order.coupon_code %></span>
        </div>
        <div style="color: #5d6481; text-align: center"></div>
        <div style="color: #5d6481; text-align: center"></div>
        <strong style="color: #19213d">- ‚Ç¨ <%= order.coupon_value %></strong>
    </div>
    <% } %>

        <div class="total-row">
            <div style="font-size: 8px; color: #5e6470; max-width: 251px">
                <!-- * Het ontbijt is niet inbegrepen in de prijs van het hotel. Extra kosten voor het ontbijt bedragen ‚Ç¨20,00 per persoon voor het volledige verblijf. -->
            </div>
            <div>
                <strong style="color: 19213D">Totaal</strong>
            </div>
            <strong style="color: #016feb; font-size: 20px">‚Ç¨ <%= order.order_total %></strong>
        </div>
    </div>

