INSERT INTO public.templates (id, name, content, subject, created_at, updated_at, created_by_id, updated_by_id, locale, description, viewer) VALUES (46, 'quote_reminder_mail', '<div style="max-width: 640px; margin: 0 auto; background-color: white">
